mapper.insert:

  SELECT blog_seq.NEXTVAL FROM dual

INSERT INTO blog_t (
    blog_no
  , title
  , contents
  , hit
  , user_no
  , create_dt
  , modify_dt
  , like_hit
) VALUES (
    #{blogNo}
  , #{title}
  , #{contents}
  , 0
  , #{userNo}
  , CURRENT_DATE
  , CURRENT_DATE
  , 0
)

INSERT INTO image_t (
    blog_no
  , upload_path
  , filesystem_name
) VALUES (
    #{blogNo}
  , #{uploadPath}
  , #{filesystemName}
)

mapper.update:
UPDATE blog_t
   SET hit = hit + 1
 WHERE blog_no = #{blogNo}

mapper.select:
SELECT COUNT(*)
  FROM blog_comment_t
 WHERE blog_no = #{blogNo}

mapper.update:

UPDATE blog_comment_t
   SET group_order = group_order + 1
 WHERE group_no = #{groupNo}
   AND group_order > #{groupOrder}

mapper.insert:

INSERT INTO blog_comment_t (
    comment_no
  , user_no
  , blog_no
  , contents
  , create_dt
  , state
  , depth
  , group_no
  , group_order
) VALUES (
    blog_comment_seq.NEXTVAL
  , #{userNo}
  , #{blogNo}
  , #{contents}
  , CURRENT_DATE
  , 1
  , #{depth}
  , #{groupNo}
  , #{groupOrder}
)

mapper.update:

UPDATE blog_t
   SET new_comment = 1
 WHERE blog_no = #{blogNo}

mapper.select:
SELECT user_no
  FROM blog_t
 WHERE blog_no = #{blogNo}

mapper.update:

UPDATE blog_t
   SET new_comment = 0
 WHERE blog_no = #{blogNo}


UPDATE blog_t
   SET like_hit = like_hit + 1
 WHERE blog_no = #{blogNo}

mapper.select:
SELECT user_no
  FROM like_t
 WHERE blog_no = #{blogNo}
   AND user_no = #{userNo}

mapper.insert:

INSERT INTO like_t (
    like_no
  , user_no
  , blog_no
) VALUES (
    like_seq.NEXTVAL
  , #{userNo}
  , #{blogNo}
)
