mapper.insert:

  SELECT movie_seq.NEXTVAL FROM dual

INSERT INTO movie_t (
    movie_no
  , movie_cd
  , movie_nm
  , audi_acc
  , sales_acc
  , open_dt
) VALUES (
    #{movieNo}
  , #{movieCd}
  , #{movieNm}
  , #{audiAcc}
  , #{salesAcc}
  , #{openDt}
)

mapper.update:
UPDATE movie_t
   SET title_eng = #{titleEng}
     , title_org = #{titleOrg}
     , plot = #{plot}
     , runtime = #{runtime}
     , rating = #{rating}
     , keywords = #{keywords}
     , genres = #{genres}
     , still_urls = #{stillUrls}
     , poster_urls = #{posterUrls}
 WHERE movie_cd = #{movieCd}

UPDATE movie_t
   SET audi_acc = #{audiAcc}
     , sales_acc = #{salesAcc}
 WHERE movie_cd = #{movieCd}

