mapper.insert:
INSERT INTO user_t (
    user_no
  , email
  , pw
  , name
  , gender
  , mobile
  , sns
  , pw_modify_dt
  , signup_dt
  , postcode
  , address
  , detailAddress
  , extraAddress
) VALUES (
    user_seq.NEXTVAL
  , #{email}
  , #{pw}
  , #{name}
  , #{gender}
  , #{mobile}
  , #{sns}
  , CURRENT_DATE
  , CURRENT_DATE
  , #{postcode}
  , #{address}
  , #{detailAddress}
  , #{extraAddress}
)

mapper.select:
SELECT user_no, email, pw, name, gender, mobile, sns, pw_modify_dt, signup_dt, postcode, address, detailAddress, extraAddress           
  FROM user_t

  email = #{email}
  AND pw = #{pw}

